// CMP Info List.
var gXWCMPInfoList;

// Create CMP Info.
function XWCMPInfo (aName, aIP, aPort, aType, aCAName, aRAName) {
	this.mName = aName;
	this.mIP = aIP;
	this.mPort = aPort;
	this.mType = aType;
	
	this.setCAName (aCAName);
	this.setRAName (aRAName);
};

XWCMPInfo.prototype = {
	setCAName : function (aCAName) {
		if (aCAName != undefined) {
			this.mCAName = aCAName;
		} else {
			this.mCAName = null;
		}
	},
	setRAName : function (aRAName) {
		if (aRAName != undefined) {
			this.mRAName = aRAName;
		} else {
			this.mRAName = null;
		}
	},
	getCAName : function () {
		return this.mCAName;
	},
	getRAName : function () {
		return this.mRAName;
	},
	getType : function () {
		return this.mType;
	},
	getIP : function () {
		return this.mIP;
	},
	getPort : function () {
		return this.mPort;
	},
	getName : function () {
		return this.mName;
	}
};

